<template>
	<div class="game_box" id="game_box">
		<div class="title">您本次游戏得分{{totalScore}}分，荣获【<span class="port">{{word}}</span>】称号</div>
		<div class="port-img">
			<img :src="levelPic" alt="">
		</div>
		<div class="level">
			<div :class="['level-box',level==1?'choosed':'']">
				<img src="../../../assets/chart/level1.png" alt="">
			</div>
			<div :class="['level-box',level==2?'choosed':'']">
				<img src="../../../assets/chart/level2.png" alt="">
			</div>
			<div :class="['level-box',level==3?'choosed':'']">
				<img src="../../../assets/chart/level3.png" alt="">
			</div>
			<div :class="['level-box',level==4?'choosed':'']">
				<img src="../../../assets/chart/level4.png" alt="">
			</div>
		</div>
		<div class="click-box">
			<div class="click-icon" @click="playAgain">
				<img src="../../../assets/chart/playAgain.png" alt="">
			</div>
			<div class="click-icon" @click="savePic">
				<img src="../../../assets/chart/create-pic.png" alt="" v-show="createPic">
				<img src="../../../assets/chart/saveHtml.png" alt="" v-show="!createPic">
			</div>
		</div>
		<div v-show="downloadShow" class="save-pic" @click="hidedownloadShow">
			<img :src="downloadUrl" alt="">
		</div>
		<div class="loading" v-show="loading">
			<img src="../../../assets/chart/loading.gif" alt="">
		</div>
		<div class='QR-code-box'>
			<div>
				<img src="data:image/jpeg;base64,"
					 alt=""/>
			</div>
			<span>扫描左侧二维码，关注“杭州南宋官窑博物馆”官方微信，带你了解更多陶瓷的奥秘</span>
		</div>
		<div class="returnBack-box" v-show="returnBackShow" @click="returnBackFun">
			<img src="data:image/png;base64,"
				 alt="">
		</div>
		<!--<a ref="download" v-show="false" :href="downloadUrl" :download="downloadfilename"></a>-->
	</div>
</template>

<script>
    import html2canvas from './html2canvas.min';

    export default {
        name: "chart",
        data() {
            return {
                loading: false,
                createPic: true,
                downloadUrl: null,
                downloadShow: false,
                downloadfilename: null,
                levelPic: "",
                word: "",
                level: "",
                returnBackShow: true,
                totalScore: 95
            }
        },
        computed: {},
        components: { //模板
        },
        mounted() { //挂载结束状态
            let totalScore = parseInt(sessionStorage.getItem("totalScore"));
            this.totalScore = totalScore;
            this.chooseLevel(totalScore);

        },
        methods: { //事件
            chooseLevel(totalScore) {
                let level, word, levelPic;
                if (totalScore > 165) {
                    levelPic = require("../../../assets/chart/personLevel4.png");
                    word = "制瓷大师";
                    level = 4;
                } else if (totalScore > 150 && totalScore < 165) {
                    levelPic = require("../../../assets/chart/personLevel3.png");
                    word = "制瓷能手";
                    level = 3;
                } else if (totalScore > 120 && totalScore < 150) {
                    levelPic = require("../../../assets/chart/personLevel2.png");
                    word = "制瓷工匠";
                    level = 2;
                } else {
                    levelPic = require("../../../assets/chart/personLevel1.png");
                    level = 1;
                    word = "制瓷学徒";
                }
                this.level = level;
                this.word = word;
                this.levelPic = levelPic;
            },
            playAgain() {
                //				this.$router.push('/diglett');
                this.$router.go(-4);
            },
            savePic() {
                // this.loading = true;
                let windowWidth = $("body").width();
                let windowHeight = $("#game_box").height();
                let self = this;
                setTimeout(() => {
                    self.createPic = false;
                }, 300);
                setTimeout(() => {
                    let inviteBox = document.getElementById("game_box");
                    let canvas = document.createElement("canvas");
                    canvas.width = windowWidth // 最终图片宽度280px的2倍，以px为单位
                    canvas.height = windowHeight // 最终图片高度315px的2倍，以px为单位
                    let opts = {
                        canvas: canvas, // 将自定义canvas作为配置项
                        useCORS: true,
                    };
                    html2canvas(inviteBox, opts).then((canvas) => {
                        let base64ImgSrc = canvas.toDataURL("image/png");
                        self.downLoadImage(base64ImgSrc);
                    })
                }, 500);
            },
            downLoadImage(imgUrl) {
                this.downloadUrl = imgUrl;
                this.downloadShow = true;
                // var link = document.createElement("a");
                // link.innerHTML = 'fileName';
                // link.download = 'fileName.jpg';
                // link.href = imgUrl;
                // let evt = document.createEvent('MouseEvents');
                // evt.initEvent('click', true, true);
                // link.dispatchEvent(evt);
            },
            hidedownloadShow() {
                this.createPic = true;
                this.downloadShow = false;
            },
            returnBackFun() {
                this.returnBackShow = false;
            },
        }
    }
</script>

<style scoped lang="less">
	@import "../../../assets/css/common.less";
	@import "./chart.less";
</style>
